jQuery(document).ready(function() {
	
	//preloading screen
	jQuery('#loader-title').addClass('loader-title fadeInDown animated');
	jQuery('#loader-description').addClass('loader-description fadeInDown animated');
	
	setTimeout(function(){
		jQuery('#loader-loading').addClass('loader-loading loader-loading-fadeIn');
	}, 1000);
	
	//disable scrolling
	jQuery('html, body').css({
		overflow: 'hidden',
		height: '100%',
	});
});