jQuery(window).load(function() {
	
	//preloading screen
	setTimeout(function(){
		jQuery('body').addClass('loaded');
		jQuery('#loader-title').css('visibility','hidden');
		jQuery('#loader-description').css('visibility','hidden');
		jQuery('#loader-loading').css('visibility','hidden');
	}, 1700);
	
	setTimeout(function(){
		jQuery('.loader-details').css('display','none');
		jQuery('#loader-title').css('display','none');
		jQuery('#loader-description').css('display','none');
		jQuery('#loader-loading').css('display','none');
		//show main content
		jQuery('.main-content').css('display','block');
		jQuery('.main-content').css('visibility','visible');
		jQuery('.main-content').css('opacity','1');
		//re-enable scrolling
		jQuery('html, body').css({
		overflow: 'auto',
		height: 'auto'
		});
	}, 1900);
	
});